DROP TABLE IF EXISTS  `prefix_admin`;
CREATE TABLE `prefix_admin` (
  `userid` mediumint(8) unsigned NOT NULL AUTO_INCREMENT COMMENT '用户ID',
  `username` varchar(20) NOT NULL DEFAULT '' COMMENT '用户名',
  `password` char(32) NOT NULL DEFAULT '' COMMENT '密码',
  `realname` varchar(20) NOT NULL DEFAULT '' COMMENT '真实姓名',
  `encrypt` char(6) NOT NULL DEFAULT '' COMMENT '加密因子',
  `roleid` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '所属角色',
  `rolepriv` text NOT NULL COMMENT '权限设定',
  `email` varchar(100) NOT NULL DEFAULT '' COMMENT 'Emai',
  `phone` char(11) NOT NULL DEFAULT '' COMMENT '手机号码',
  `lastlogintime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '最后登陆时间',
  `lastloginip` char(15) NOT NULL DEFAULT '' COMMENT '最后登陆IP',
  `logincount` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '累计登录次数',
  `inputtime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `updatetime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '最后更新',
  `qq` varchar(255) NOT NULL DEFAULT '',
  `fee_type` tinyint(2) DEFAULT NULL COMMENT '提成方式(1,缴纳保证金，2：单笔订单提成)',
  `service_fee` double(5,2) DEFAULT NULL COMMENT '提取百分比',
  `company_config` text COMMENT '商家等级奖励',
  `fee_money` double(11,2) DEFAULT '0.00' COMMENT '基本工资',
  PRIMARY KEY (`userid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_admin_news`;
CREATE TABLE `prefix_admin_news` (
  `id` int(15) NOT NULL AUTO_INCREMENT COMMENT '消息id',
  `type` int(11) DEFAULT NULL,
  `body` text COMMENT '消息内容',
  `time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `state` int(11) DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS  `prefix_admin_panel`;
CREATE TABLE `prefix_admin_panel` (
  `menuid` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `userid` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `name` char(32) DEFAULT NULL,
  `url` char(255) DEFAULT NULL,
  `datetime` int(10) unsigned DEFAULT '0',
  PRIMARY KEY (`menuid`),
  UNIQUE KEY `userid` (`menuid`,`userid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_admin_role`;
CREATE TABLE `prefix_admin_role` (
  `roleid` tinyint(3) unsigned NOT NULL AUTO_INCREMENT COMMENT '角色id',
  `rolename` varchar(50) NOT NULL DEFAULT '' COMMENT '角色名称',
  `description` text NOT NULL COMMENT '角色描述',
  `listorder` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '角色排序',
  `disabled` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '角色状态',
  `role_priv` text NOT NULL COMMENT '权限设定',
  `updatetime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  PRIMARY KEY (`roleid`),
  KEY `listorder` (`listorder`),
  KEY `disabled` (`disabled`)
) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

