DROP TABLE IF EXISTS  `prefix_announce`;
CREATE TABLE `prefix_announce` (
  `announceid` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '公告标题',
  `content` text NOT NULL COMMENT '公告内容',
  `starttime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '开始时间',
  `endtime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '结束时间',
  `inputtime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',
  `tenantid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '商家id',
  `hits` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '点击量',
  `passed` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否审核',
  `listorder` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `updatetime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '修改时间',
  `username` varchar(50) NOT NULL DEFAULT '' COMMENT '录入者',
  `type` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '0为普通公告   1为商家公告 2招商公告',
  PRIMARY KEY (`announceid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_appeal`;
CREATE TABLE `prefix_appeal` (
  `id` mediumint(8) NOT NULL AUTO_INCREMENT,
  `order_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '订单id',
  `order_sn` bigint(15) unsigned NOT NULL DEFAULT '0' COMMENT '淘宝订单号',
  `goods_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '商品ID',
  `buyer_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '申诉者id',
  `seller_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '商家ID',
  `buyer_imgs_url` mediumtext NOT NULL COMMENT '申诉图片证据',
  `buyer_cause` tinytext NOT NULL COMMENT '申诉原因',
  `appeal_status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '状态(0:待商家补充凭证，1:待平台仲裁,2:平台仲裁完毕,3:已关闭申诉,4:系统自动审核通过）',
  `appeal_type` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '申诉类型（1：修改单号；2：单号被审核有误；3:申请取消资格;4:其它）',
  `buyer_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '买家申诉时间',
  `seller_cause` mediumtext NOT NULL COMMENT '商家回复原因',
  `seller_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商家操作时间',
  `admin_cause` mediumtext NOT NULL COMMENT '管理员审核原因',
  `admin_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '管理员操作时间',
  `buyer_phone` bigint(12) unsigned NOT NULL DEFAULT '0' COMMENT '申诉人电话',
  `buyer_qq` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '申诉人QQ',
  `buyer_ww` varchar(50) NOT NULL DEFAULT '' COMMENT '申诉人旺旺',
  `seller_imgs_url` mediumtext NOT NULL COMMENT '商家图片证据',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_article`;
CREATE TABLE `prefix_article` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `catid` smallint(5) unsigned NOT NULL DEFAULT '0',
  `title` char(80) NOT NULL DEFAULT '',
  `style` char(24) NOT NULL DEFAULT '',
  `thumb` char(100) NOT NULL DEFAULT '',
  `keywords` char(40) NOT NULL DEFAULT '',
  `description` char(255) NOT NULL DEFAULT '',
  `posids` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `url` char(100) NOT NULL,
  `listorder` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `status` tinyint(2) unsigned NOT NULL DEFAULT '1',
  `sysadd` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `islink` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `username` char(20) NOT NULL,
  `inputtime` int(10) unsigned NOT NULL DEFAULT '0',
  `updatetime` int(10) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `status` (`status`,`listorder`,`id`),
  KEY `listorder` (`catid`,`status`,`listorder`,`id`),
  KEY `catid` (`catid`,`status`,`id`)
) ENGINE=MyISAM AUTO_INCREMENT=269 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

