DROP TABLE IF EXISTS  `prefix_badword`;
CREATE TABLE `prefix_badword` (
  `badid` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `badword` char(20) NOT NULL,
  `level` tinyint(5) NOT NULL DEFAULT '1',
  `replaceword` char(20) NOT NULL DEFAULT '0',
  `lastusetime` int(10) unsigned NOT NULL DEFAULT '0',
  `listorder` tinyint(3) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`badid`),
  UNIQUE KEY `badword` (`badword`),
  KEY `usetimes` (`replaceword`,`listorder`),
  KEY `hits` (`listorder`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_brand`;
CREATE TABLE `prefix_brand` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL DEFAULT '' COMMENT '品牌名称',
  `image` varchar(50) NOT NULL DEFAULT '' COMMENT '图片地址',
  `url` varchar(255) NOT NULL DEFAULT '' COMMENT '链接地址',
  `dateline` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',
  `listorder` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `updatetime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_cash_records`;
CREATE TABLE `prefix_cash_records` (
  `cashid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '提现id',
  `userid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '用户id',
  `money` decimal(9,2) NOT NULL COMMENT '提现金额',
  `inputtime` int(10) NOT NULL COMMENT '申请提现时间',
  `check_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '审核时间',
  `status` tinyint(2) NOT NULL DEFAULT '0' COMMENT '提现状态(-1:审核未通过;0:待审核;1:审核通过;)',
  `cause` tinytext NOT NULL COMMENT '审核未通过原因',
  `ip` char(15) NOT NULL DEFAULT '' COMMENT '用户ip',
  `cash_alipay_username` char(50) NOT NULL DEFAULT '' COMMENT '提现的账号',
  `type` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '提现方式 1 提现到银行 2 提现到支付宝',
  `paypal` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '提现模式 1 普通 2快速',
  `fee` double(5,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '手续费',
  `totalmoney` double(8,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '实际提现金额',
  `name` varchar(50) NOT NULL DEFAULT '' COMMENT '提现姓名',
  `bank` varchar(255) NOT NULL DEFAULT '' COMMENT '提现银行',
  `operator` varchar(50) NOT NULL DEFAULT '' COMMENT '操作人',
  `source` tinyint(3) DEFAULT '0' COMMENT '操作来源',
  PRIMARY KEY (`cashid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_category`;
CREATE TABLE `prefix_category` (
  `catid` smallint(5) unsigned NOT NULL AUTO_INCREMENT COMMENT '栏目ID',
  `type` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '类别',
  `modelid` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '模型ID',
  `domain` varchar(200) DEFAULT NULL COMMENT '栏目绑定域名',
  `parentid` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '父ID',
  `arrparentid` varchar(255) NOT NULL COMMENT '所有父ID',
  `child` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否存在子栏目，1存在',
  `arrchildid` mediumtext NOT NULL COMMENT '所有子栏目ID',
  `catname` varchar(30) NOT NULL COMMENT '栏目名称',
  `image` varchar(100) NOT NULL COMMENT '栏目图片',
  `description` mediumtext NOT NULL COMMENT '栏目描述',
  `parentdir` varchar(100) NOT NULL COMMENT '父目录',
  `catdir` varchar(30) NOT NULL COMMENT '栏目目录',
  `url` varchar(100) NOT NULL COMMENT '链接地址',
  `hits` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '栏目点击数',
  `setting` mediumtext NOT NULL COMMENT '相关配置信息',
  `listorder` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `ismenu` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否显示',
  `ishtml` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否生成静态',
  `letter` varchar(30) NOT NULL COMMENT '栏目拼音',
  `updatetime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `usable_type` varchar(255) NOT NULL DEFAULT '',
  `items` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '数据量',
  PRIMARY KEY (`catid`),
  KEY `module` (`parentid`,`listorder`,`catid`)
) ENGINE=MyISAM AUTO_INCREMENT=90 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

