DROP TABLE IF EXISTS  `prefix_focus`;
CREATE TABLE `prefix_focus` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '图片标题',
  `image` varchar(255) NOT NULL DEFAULT '' COMMENT '图片路径',
