DROP TABLE IF EXISTS  `prefix_product`;
CREATE TABLE `prefix_product` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `mod` varchar(50) NOT NULL DEFAULT '0' COMMENT '活动类型',
  `catid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '产品分类ID',
  `start_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '开始时间',
  `end_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '结束时间',
  `status` tinyint(1) NOT NULL DEFAULT '-3' COMMENT '活动状态',
  `userid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '商家ID',
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '活动标题',
  `keyword` varchar(250) NOT NULL DEFAULT '' COMMENT '商品关键字',
  `company_id` mediumint(200) unsigned NOT NULL DEFAULT '0' COMMENT '所属商家',
  `issystem` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否后台发布',
  `thumb` char(100) NOT NULL DEFAULT '' COMMENT '缩略图',
  `username` char(50) NOT NULL DEFAULT '' COMMENT '用户名',
  `inputtime` int(10) NOT NULL,
  `updatetime` int(10) NOT NULL,
  `already_num` int(10) unsigned NOT NULL COMMENT '已抢购数量',
  `hits` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '浏览量',
  `isrecommend` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否为推荐商品',
  `goods_price` double(8,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '下单价格',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_product_brand`;
CREATE TABLE `prefix_product_brand` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `modelid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '所属活动',
  `catid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '产品分类ID',
  `start_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '开始时间',
  `end_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '结束时间',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '活动状态',
  `userid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '商家ID',
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '活动标题',
  `issystem` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否后台发布',
  `thumb` char(100) NOT NULL DEFAULT '' COMMENT '缩略图',
