DROP TABLE IF EXISTS  `prefix_product_commission`;
CREATE TABLE `prefix_product_commission` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL DEFAULT '',
  `ctype` varchar(255) DEFAULT NULL,
  `source` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `sort` tinyint(1) unsigned NOT NULL DEFAULT '1',
  `goods_address` varchar(255) NOT NULL DEFAULT '',
  `goods_wangwang` varchar(255) NOT NULL DEFAULT '',
  `goods_number` int(10) unsigned NOT NULL DEFAULT '0',
  `goods_price` double(10,2) unsigned NOT NULL,
  `goods_search_albums_url` text NOT NULL,
  `thumb` varchar(255) NOT NULL DEFAULT '',
  `goods_service` double(15,2) unsigned NOT NULL DEFAULT '0.00',
  `bonus_price` double(15,2) unsigned NOT NULL DEFAULT '0.00',
  `goods_url` text NOT NULL,
  `goods_tips` varchar(255) DEFAULT NULL,
  `goods_deposit` double(15,2) DEFAULT NULL,
  `activity_days` tinyint(3) NOT NULL,
  `goods_content` text COMMENT '商品详情',
  `subsidy` varchar(100) DEFAULT NULL COMMENT '补贴数',
  `subsidy_type` int(10) DEFAULT NULL COMMENT '补贴类型（1：积分，2：金额）',
  `allow_groupid` varchar(150) DEFAULT NULL COMMENT '允许参与',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_product_complement`;
CREATE TABLE `prefix_product_complement` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `product_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '所属产品',
  `company_id` mediumint(6) unsigned NOT NULL DEFAULT '0' COMMENT '所属商家',
  `com_number` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '补足份数',
  `com_day` smallint(3) unsigned NOT NULL DEFAULT '0' COMMENT '追加天数',
  `com_total_fee` double(8,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '保证金费用',
  `dateline` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '提交时间',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '当前状态（0：待支付；1：已支付）',
  PRIMARY KEY (`id`),
  KEY `pcs` (`product_id`,`company_id`,`status`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_product_log`;
CREATE TABLE `prefix_product_log` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `p_id` varchar(20) NOT NULL DEFAULT '' COMMENT '产品ID',
  `p_state` tinyint(3) NOT NULL DEFAULT '0' COMMENT '状态标识',
  `is_sys` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否为后台 1是 0否',
  `uid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '操作人',
  `msg` text NOT NULL COMMENT '操作理由',
  `dateline` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '操作时间',
  `clientip` char(15) NOT NULL DEFAULT '' COMMENT '操作IP',
  PRIMARY KEY (`id`),
  KEY `p_id` (`p_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_product_postal`;
CREATE TABLE `prefix_product_postal` (
  `id` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `source` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '店铺来源',
  `taobaoke` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否参与淘宝客推广',
  `goods_albums` text NOT NULL COMMENT '商品多图',
  `goods_number` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品份数',
  `goods_price` double(8,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '下单价格',
  `goods_tips` text NOT NULL COMMENT '温馨提醒',
  `goods_content` text NOT NULL COMMENT '商品介绍',
  `goods_url` text NOT NULL COMMENT '下单地址',
  `taobao_charge` double(5,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '淘宝客佣金',
  `activity_days` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '活动时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_product_rebate`;
CREATE TABLE `prefix_product_rebate` (
  `id` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `source` mediumint(8) unsigned NOT NULL DEFAULT '1' COMMENT '店铺来源(1:淘宝；2：天猫)',
  `type` varchar(50) NOT NULL DEFAULT '' COMMENT '下单方式',
  `taobaoke` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否参与淘宝客推广',
  `goods_albums` text NOT NULL COMMENT '商品多图',
  `goods_number` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品份数',
  `goods_price` double(8,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '下单价格',
  `goods_discount` float(2,1) unsigned NOT NULL DEFAULT '0.0' COMMENT '商品折扣',
  `activity_days` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '活动时间',
  `goods_content` text NOT NULL COMMENT '商品介绍',
  `goods_url` text NOT NULL COMMENT '下单地址',
  `goods_tips` text NOT NULL COMMENT '温馨提醒',
  `goods_rule` text NOT NULL COMMENT '下单规则',
  `goods_extra` double(8,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '额外奖励',
  `goods_deposit` double(8,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '保证金',
  `goods_service` double(5,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '平台服务费（%）',
  `service_type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '收费方式 1.全额收取 2.只收取部分返还费用',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_product_special`;
CREATE TABLE `prefix_product_special` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `modelid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '所属活动',
  `catid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '产品分类ID',
  `start_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '开始时间',
  `end_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '结束时间',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '活动状态',
  `userid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '商家ID',
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '活动标题',
  `issystem` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否后台发布',
  `thumb` char(100) NOT NULL DEFAULT '' COMMENT '缩略图',
  `username` char(50) NOT NULL DEFAULT '' COMMENT '用户名',
  `inputtime` int(10) NOT NULL,
  `updatetime` int(10) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_product_trial`;
CREATE TABLE `prefix_product_trial` (
