  `goods_content` text NOT NULL COMMENT '商品详情',
  `goods_deposit` double(8,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '总保证金',
  `goods_service` double(8,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '推广费用(费用)',
  `goods_rule` text NOT NULL COMMENT '下单规则',
