  `trial_type` tinyint(3) DEFAULT NULL COMMENT '试用类型',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS  `prefix_push_log`;
